    json: {
      ...data,
      stageBriefings,
    },
  },
];

const data = $json;

const promptMessages = Object.entries(data.prompts).map(([key, prompt]) => ({
  id: key,
  titulo: prompt.titulo,
  objetivo: prompt.objetivo,
  prompt: prompt.texto,
}));

const copyPromptLines = [
