  },
];

const data = $json;

const checklistResumo = data.checklist
  .map((section) => {
    const tasks = section.tasks
      .map((task) => '- [ ] ' + task.task + ' (' + task.owner + ')')
      .join('\n');
    return '### ' + section.area + '\n' + tasks;
  })
  .join('\n\n');

