      'Meta: ' + stage.alvo + '\n' +
      'Indicador-chave: ' + stage.indicador + '\n' +
      'Top ações IA:\n' + foco + '\n' +
      'Ferramentas sugeridas:\n' + ferramentas;
  })
  .join('\n\n');

const promptsResumo = data.promptMessages
  .map((p) => '### ' + p.titulo + '\n' + 'Objetivo: ' + p.objetivo + '\n\n' + p.prompt)
  .join('\n\n');

