}
if (data.metrics && data.metrics.resultadosNegocio) {
  data.metrics.resultadosNegocio.forEach((m) => {
    metricas.push('- Resultados: ' + m);
  });
}
if (data.metrics && data.metrics.competitividade) {
  data.metrics.competitividade.forEach((m) => {
    metricas.push('- Competitividade: ' + m);
  });
}

const summaryLines = [
  '# Plano Semanal IA First',
  '',
  '## Checklist imediato',
  checklistResumo,
  '',
  '## Agenda semanal sugerida',
