return [
  {
    json: {
      agentId: 'analista_funil',
      description: 'Gera prompt consolidado para analista virtual (OpenAI, Claude, etc.).',
      prompt: promptLines.join('\n'),
      stageBriefings: data.stageBriefings || [],
      metrics: data.metrics || {},
    },
  },
];

const missingNodes = [];

function resolveNodeJson(baseName) {
  const candidates = [];
  candidates.push(baseName);
  for (let i = 1; i <= 5; i++) {
    candidates.push(baseName + i);
    candidates.push(baseName + ' (' + i + ')');
    candidates.push(baseName + ' Copy ' + i);
  }

  for (let index = 0; index < candidates.length; index++) {
    const candidate = candidates[index];
    try {
      const items = $items(candidate, 0, 0) || [];
      if (items.length > 0 && items[0].json) {
        return items[0].json;
      }
    } catch (error) {
      continue;
    }
  }

  missingNodes.push(baseName);
  return {};
}

const digest = resolveNodeJson('Agent 1 - Digest Builder');
const planner = resolveNodeJson('Agent 2 - Task Planner');
const base = resolveNodeJson('Report Payload');
const promptPack = resolveNodeJson('Prompt Pack');
const insight = $json || {};

const context = {
  resumoSemanal: digest.text,
  promptSugerido: insight.prompt,
  planoSemanal: planner.sprintPlan,
  checklist: planner.tasks,
  etapasFunil: base.stageBriefings,
  metricas: base.metrics,
  toolkit: base.toolkit,
  promptsDisponiveis: promptPack.promptMessages,
  markdownCompleto: base.payload ? base.payload.markdown : null,
};

if (missingNodes.length > 0) {
  context.__missingSources = missingNodes;
}

const instructions = [
  'Você é o Chief AI Operator responsável por gerar decisões acionáveis.',
  '1. Leia o contexto JSON fornecido.',
  '2. Gere um plano priorizado de 5 ações para a semana.',
  '3. Identifique riscos por etapa do funil e proponha mitigação.',
  '4. Sugira 3 experimentos de IA que podem gerar ganhos rápidos.',
  '5. Resuma em bullets o que deve ser comunicado à diretoria.',
  '6. Se notar dados faltando, solicite explicitamente no output.',
];

const systemMessage = 'Você é o Chief AI Operator da vertical de lançamentos. Use tom consultivo, direto, com foco em performance e impactos mensuráveis.';
const userMessage = instructions.join('\n') + '\n\nContexto base JSON:\n' + JSON.stringify(context, null, 2);

return [
  {
    json: {
      systemMessage,
      userMessage,
      context,
    },
  },
];
